import React, { useState } from 'react';
import axios from 'axios';

function TrailSearch() {
  const [trails, setTrails] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get(`http://localhost:3001/api/trails/search?query=${searchTerm}`);
      setTrails(response.data);
    } catch (err) {
      console.error('Error searching trails:', err);
    }
  };

  const handleSaveTrail = (placeId) => {
    const savedTrails = JSON.parse(localStorage.getItem('savedTrails')) || [];
    if (!savedTrails.includes(placeId)) {
      savedTrails.push(placeId);
      localStorage.setItem('savedTrails', JSON.stringify(savedTrails));
    }
  };

  return (
    <div>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search for trails"
          required
        />
        <button type="submit">Search</button>
      </form>
      <ul>
        {trails.map((trail) => (
          <li key={trail.place_id}>
            <h3>{trail.name}</h3>
            <p>{trail.description}</p>
            <button onClick={() => handleSaveTrail(trail.place_id)}>Save Trail</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TrailSearch;
