import React, { useState } from 'react';
import axios from 'axios';

function SearchTrails() {
  const [query, setQuery] = useState('');
  const [location, setLocation] = useState('');
  const [radius, setRadius] = useState('');
  const [trails, setTrails] = useState([]);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get('http://localhost:3000/api/trail/searchTrails', {
        params: { query, location, radius }
      });
      setTrails(response.data.results);
    } catch (err) {
      console.error('Error searching trails:', err);
    }
  };

  return (
    <div>
      <h1>Search Trails</h1>
      <form onSubmit={handleSearch}>
        <label htmlFor="query">Query:</label>
        <input
          type="text"
          id="query"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Enter search query"
        />
        <br />
        <label htmlFor="location">Location:</label>
        <input
          type="text"
          id="location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          placeholder="Enter location (lat,lng)"
        />
        <br />
        <label htmlFor="radius">Radius (meters):</label>
        <input
          type="number"
          id="radius"
          value={radius}
          onChange={(e) => setRadius(e.target.value)}
          placeholder="Enter search radius"
        />
        <br />
        <button type="submit">Search</button>
      </form>
      <h2>Results</h2>
      <ul>
        {trails.map((trail) => (
          <li key={trail.place_id}>
            <h3>{trail.name}</h3>
            <p>{trail.formatted_address}</p>
            <p>Rating: {trail.rating}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default SearchTrails;
